import axios from 'axios';
// import qs from 'qs';

// const baseURL = 'https://www.camptogo.com';

export const pro_create = ({ commit }, data) => {
    // eslint-disable-next-line no-unused-vars
    return new Promise((resolve, reject) => {
        let id = 0;
        axios.post(
            '/api/test/72b88c04309911ec8d3d0242ac130003/v1_0_0/testprovider/create',
            {
                name: data.name,
                describe: data.desc,
                exclusive: data.exclusive,
                mobile: data.mobile,
                email: data.email,
                wechat: data.wechat
            },
            {
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': 'Basic ' + btoa('testeradmin' + ':' + 'testerpassword')
                }
            }).then(res => {
                console.log(res.data);
                console.log(res.data.details);
             id = res.data.details.id;
             console.log(id);
        }).catch(err => {
            console.log(err);
        });
        commit('SET_PROVIDER', data, id);
    })
}

export const pro_get = ({ commit }, id) => {
    // eslint-disable-next-line no-unused-vars
    return new Promise((resolve, reject) => {
        let data = {};
        axios.post(
            'https://www.camptogo.com/test/72b88c04309911ec8d3d0242ac130003/v1_0_0/testprovider/get',
            {
                id: data.id,
            },
            {
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                },
                auth: {
                    Username: 'testeradmin',
                    Password: 'testerpassword'
                }
            }).then(res => {
            data = res.data;
        }).catch(err => {
            console.log(err);
        });
        commit('SET_PROVIDER', data, id);
    })
}

export const pro_update = ({ commit }, data) => {
    // eslint-disable-next-line no-unused-vars
    return new Promise((resolve, reject) => {

        axios.post(
            'https://www.camptogo.com//test/72b88c04309911ec8d3d0242ac130003/v1_0_0/testprovider/update',
            {
                id: data.id,
                name: data.name,
                describe: data.desc,
                exclusive: data.exclusive,
                mobile: data.mobile,
                email: data.email,
                wechat: data.wechat
            },
            {
                headers: {
                    'content-type': 'application/x-www-form-urlencoded'
                },
                auth: {
                    Username: 'testeradmin',
                    Password: 'testerpassword'
                }
            }).then(res => {
            data = res.data;
        }).catch(err => {
            console.log(err);
        });
        commit('SET_PROVIDER', data, data.id);
    })
}

