import axios from 'axios';

// const baseURL = 'https://www.camptogo.com';

export const pro_create = ({ commit }, data) => {
    // eslint-disable-next-line no-unused-vars
    return new Promise((resolve, reject) => {
        let id = 0;
        var username = 'testeradmin';
        var password = 'testerpassword';
        var basicAuth = 'Basic ' + btoa(username + ':' + password);
        axios.post(
            'https://www.camptogo.com/test/72b88c04309911ec8d3d0242ac130003/v1_0_0/testprovider/create',
            {
                name: data.name,
                describe: data.desc,
                exclusive: data.exclusive,
                mobile: data.mobile,
                email: data.email,
                wechat: data.wechat
            },
            {
                headers: {
                    "Content-Type": "application/x-www-form-urlencoded",
                    Authorization: basicAuth
                }
                // auth: {
                //     username: 'testeradmin',
                //     password: 'testerpassword'
                // }
            }).then(res => {
             id = res.data.details.id;
             console.log(id);
        }).catch(err => {
            console.log(err);
        });
        commit('SET_PROVIDER', data, id);
    })
}

