import axios from 'axios';
import {SET_PROVIDER} from "@/vuex/mutations";

const baseURL = 'https://www.camptogo.com';

axios.defaults.baseURL = baseURL;

axios.defaults.baseURL = 20000;

axios.defaults.headers.post['Content-Type'] = 'application/x-www-form-urlencoded';

export const pro_create = ({ commit }, data) => {
    // eslint-disable-next-line no-unused-vars
    return new Promise((resolve, reject) => {
        let id = 0;
        axios.post(
            `baseURL + '/test/72b88c04309911ec8d3d0242ac130003/v1_0_0/testprovider/create'`,
            {
                "name": data.name,
                "describe": data.desc,
                "exclusive": data.exclusive,
                "mobile": data.mobile,
                "email": data.email,
                "wechat": data.wechat
            },
            {
                headers: {
                    "Content-Type": "application/x-www-form-urlencoded"
                },
                auth: {
                    Username: 'testeradmin',
                    Password: 'testerpassword'
                }
            }).then(res => {
             id = res.data.details.id;
             console.log(id);
        }).catch(err => {
            console.log(err);
        });
        commit(SET_PROVIDER, data, id);
    })
}

